package com.nerdz.yetanotherweatherapp.models

import com.google.common.truth.Truth
import com.google.gson.Gson
import org.junit.Test

class DataTest {
    val gson = Gson()
    val forecast = gson.fromJson(rawData, Forecast::class.java)
    val data = forecast.list.first()
    val dt = data.dt

    @Test
    fun getLocalDateTimeReturnsCorrectDateTime() {
        val localDate = data.getLocalDateTime().toLocalDate()
        val localTime = data.getLocalDateTime().toLocalTime()
        Truth.assertThat(localDate.toString()).isEqualTo("2021-04-29")
        Truth.assertThat(localTime.toString()).isEqualTo("02:00")
    }

    @Test
    fun getLocalDateTimeFormattedCorrectlyFormats() {
        val localFormatted = data.getLocalDateTimeFormatted()
        Truth.assertThat(localFormatted).isEqualTo("29 APRIL - 02:00")
    }

    @Test
    fun getLocalDateReturnsCorrectDate() {
        val localDate = data.getLocalDate()
        Truth.assertThat(localDate.toString()).isEqualTo("2021-04-29")
    }

    @Test
    fun getFormattedTempCorrectlyFormats() {
        val formattedTemp = data.getFormattedTemp()
        Truth.assertThat(formattedTemp).isEqualTo("10°")
    }

    @Test
    fun getFormattedFeelCorrectlyFormats() {
        val formattedFeel = data.getFormattedFeel()
        Truth.assertThat(formattedFeel).isEqualTo("9°")
    }

    @Test
    fun getInterpretationCorrectlyInterprets() {
        val interpretationCold = data.getInterpretation()
        Truth.assertThat(interpretationCold).isEqualTo("COLD")

        val interpretationHot = forecast.list[4].getInterpretation()
        Truth.assertThat(interpretationHot).isEqualTo("HOT")
    }

    @Test
    fun getIconURLReturnsCorrectUrl() {
        val iconUrl = data.getIconURL()
        Truth.assertThat(iconUrl).isEqualTo("https://openweathermap.org/img/wn/03n.png")
    }

